import L from 'leaflet'
import fetch from 'isomorphic-unfetch'
import React, { Component } from 'react'

const elId = 'map'

const style = {
    default: {
        color: '#0055B8',
        weight: 1,
        opacity: 1,
        fillColor: 'red',
        fillOpacity: 1
    },
    hover: {
        color: '#0055B8',
        weight: 4,
        opacity: .6,
        fillColor: 'red',
        fillOpacity: 1
    },
    selected: {
        color: '#0055B8',
        weight: 1,
        opacity: 1,
        fillColor: '#0055B8',
        fillOpacity: 1
    }
}

class Map extends Component {
    constructor(props) {
        super(props)

    }

    async componentDidMount() {

        const mapData = await fetch(`/data/maakonnad.geojson`).then(response => response.json());
        console.log(mapData);
        const map = L.map(elId, {
            attributionControl: false,
            zoomControl: false,
            zoomSnap: 0
        });


        const maakondLayer=L.geoJSON(mapData, {
            style: style.default
        }).addTo(map);
        map.fitBounds(maakondLayer.getBounds(),  {padding: [0, 0]});


    }

    render() {
        return <div style={{height: 500, width: 500 }} id="map" className="map">
        </div>
    }


}

export default Map
