import L from 'leaflet'
import fetch from 'isomorphic-unfetch'
import React, { Component } from 'react'

const elId = 'map'

const style = {
    default: {
        color: '#0055B8',
        weight: 1,
        opacity: 1,
        fillColor: 'red',
        fillOpacity: 1
    },
    hover: {
        color: '#0055B8',
        weight: 4,
        opacity: .6,
        fillColor: 'red',
        fillOpacity: 1
    },
    selected: {
        color: '#0055B8',
        weight: 1,
        opacity: 1,
        fillColor: '#0055B8',
        fillOpacity: 1
    }
}

class Map extends Component {
    constructor(props) {
        super(props)

    }

    async componentDidMount() {
        const mapData = await fetch(`/data/maakond.geojson`).then(response => response.json());
        console.log(mapData.features[0]);

        const map = new L.Map('map', {
            'center': [0, 0],
            'zoom': 0
        });


        // add the OpenStreetMap tiles
       /* L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            maxZoom: 19,
            attribution: '&copy; <a href="https://openstreetmap.org/copyright">OpenStreetMap contributors</a>'
        }).addTo(map);*/

        const myStyle = {
            "color": "#ff7800",
            "weight": 5,
            "opacity": 0.65
        };



       const layer = L.GeoJSON(mapData, {
            style: myStyle
        }).addTo(map);
        // Fit map to bounds of your GeoJSON
        map.fitBounds(layer.getBounds());
    }

    render() {
        return <div style={{height: 500 }} id="map" className="map">
        </div>
    }



    createMap(elId) {

        // disable drag and zoom handlers
        map.dragging.disable()
        map.touchZoom.disable()
        map.doubleClickZoom.disable()
        map.scrollWheelZoom.disable()
        return map
    }





    createLayer(id, options) {
        this.setState({
            id: id,
            labelFeatures: options.labels
        })
        this.state.layer.clearLayers()
        this.state.labels.clearLayers()
        this.load(id)
            .then(response => {
                const data = Object.assign({}, response)
                if ('filters' in options && options.filters.length) {
                    data.features = data.features.filter(feature => {
                        const valid = []
                        options.filters.forEach(item => {
                            Object.keys(item).forEach(key => {
                                if (key in feature.properties) {
                                    valid.push(feature.properties[key] === item[key])
                                }
                            })
                        })
                        return valid.indexOf(true) > -1
                    })
                }
                if ('selected' in options && options.selected.length) {
                    data.features = data.features.map(feature => {
                        const valid = []
                        options.selected.forEach(item => {
                            Object.keys(item).forEach(key => {
                                if (key in feature.properties) {
                                    valid.push(feature.properties[key] === item[key])
                                }
                            })
                        })
                        if (valid.indexOf(true) > -1) {
                            feature._selected = true
                        }
                        return feature
                    })
                }
                this.state.layer.addData(data)
                this.state.map.fitBounds(this.state.layer.getBounds(), {padding: [0, 0]})
            })
    }

}

export default Map
